                frameBorder="0"></iframe>  
        <CommentsPanel videoId={videoId} /> 
        <CommentForm videoId={videoId}/>
        {/*<Store>*/}
        <SearchBar />
    </div>

    <div className = "nav flex-column col-3" id  = "relatedvideos">
        <RelatedVideos videoId={videoId} />
    </div>

    </div>
    )

}

export default App;
