import axios from 'axios';
import cors from 'cors';
import './SearchBar.css';
import React, { useContext, useEffect, useState } from 'react';
require('dotenv').config();


const SearchBar = (props) => {


    const [searchTerm, setSearchTerms] = useState("");   

    // const express = require('express');
    // const app = express();
    // app.use(cors())

    const handleSearch = event => {
            event.preventDefault();
            setSearchTerms(event.target.value);
            console.log(searchTerm);
            axios({
              "method": "GET",
              "url": 'https://www.googleapis.com/youtube/v3/',
              "params":{
                  'part':'snippet',
                  'maxResults':'2',
                  'key':"AIzaSyCh6Bf8986v3tb2o3as3LAKIkw8Yc_iCmQ",
                  'q': {searchTerm}
              }
          })
          .then(resp => {
              const videoData = resp.data;
              console.log(videoData)

      }).catch(err => {
          console.log(err);
      });
  };



    return (

               <nav className="ytNavbar">
                <form 
                  className="navbar-form navbar-left" 
                  role="search"
                  onSubmit={(e) => handleSearch(e)}>
                    <img src="https://i.ibb.co/26BswNR/pngfind-com-youtube-png-986.png" alt="pngfind-com-youtube-png-986" width= "90" border="0"></img>
                    <div className="form-group">
                      <input 
                          className="form-control" 
                          type="text"
                          placeholder="search"
                          name="input"
                          value={searchTerm}
                          onChange={(e) => setSearchTerms(e.target.value)}>
                      </input>
                    </div>
                  <button 
                    type="submit"
                    className="btn btn-default"
                    >Search</button>
            </form>
            </nav> 


    );


    };




export default SearchBar;   

