import React, { useEffect, useState } from 'react';
require('dotenv').config();
const axios = require('axios');
// import ReactDOM from 'react-dom';



function SearchBar() {
    const [searchTerm, setSearchTerms] = useState("");   


    const handleSearch = event => {
            event.preventDefault();
            setSearchTerms(event.target.value);
            console.log(searchTerm);

    }

    useEffect(() => {
        axios({
            "method": "GET",
            "url": 'https://www.googleapis.com/youtube/v3/search',
            "params":{
                'part':'snippet',
                'maxResults':'2',
                'key':"AIzaSyCh6Bf8986v3tb2o3as3LAKIkw8Yc_iCmQ",
                'q': searchTerm
            }
        })
        .then(resp => {
            const videoData = resp.data;
            console.log(videoData)

    }).catch(err => {
        console.log(err);
    });
});



    return (
        <div>
            <nav class="navbar navbar-light bg-light">
            <img src="https://i.ibb.co/26BswNR/pngfind-com-youtube-png-986.png" alt="pngfind-com-youtube-png-986" width= "90" border="0"></img>                <form onSubmit={(e) => handleSearch(e)}>
                    <input 
                        icon='search'
                        type="text"
                        length= "200px"
                        placeholder="search"
                        name="input"
                        value={searchTerm}
                        onChange={(e) => setSearchTerms(e.target.value)}
                    />
                    <button type="submit">Search</button>
            </form>
        </nav>
    </div>
    );


    }



export default SearchBar;   

